import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Recipe } from 'src/app/models/recipe.model';
import { RecipeService } from 'src/app/services/recipe.service';


@Component({
  selector: 'app-detail',
  templateUrl: './detail.component.html',
  styleUrls: ['./detail.component.scss']
})
export class DetailComponent implements OnInit{

  percorsoStelline = '../../../../assets/images/difficolta-';
  ricetta: Recipe;


  constructor(
    private activatedRoute: ActivatedRoute,
    private router: Router,
    private recipeService: RecipeService
  ){}

  onGetRecipe(): void{
    const id = Number(this.activatedRoute.snapshot.paramMap.get('_id'));

    this.recipeService.getRecipe(id).subscribe({
      next: (res)=> {
        this.ricetta = res;
      },
      error: (err) => {
        console.log(err);
      }
    })
  }

  onGetRecipe2(): void {
    this.activatedRoute.params.subscribe((parametriUrl) => {
      const id = Number(parametriUrl['_id']); 

      this.recipeService.getRecipe(id).subscribe({
        next: (res)=> {
          this.ricetta = res;
        },
        error: (err) => {
          console.log(err);
        }
      })
    })
  }

  ngOnInit(): void{
    this.onGetRecipe();
  }



}
<div class="container-dettaglio">
    <div class="immagine" [ngStyle]="{'background-image': 'url(' + ricetta.image +')'}"></div>
    <div class="container-ricetta">
        <h2>{{ricetta.title}}</h2>
        <p>{{ricetta.description}}</p>
    </div>
    <div class="row">
        <strong>Difficoltà:</strong>
        <img [src]="percorsoStelline + ricetta._id + '.png'" alt="" class="difficolta">
    </div>
    <div class="data-pubblicazione">
        <h5>Ricetta pubblicata il {{ricetta.date}}</h5>
    </div>
</div>
