import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service';
import { MessageService } from 'primeng/api';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss'],
  providers: [MessageService],
})
export class LoginComponent {
  loginError: string;
  user: any;

  constructor(
    private authService: AuthService, 
    private router: Router,
    private messageService: MessageService,){}

  onSubmit(credentials: any){
    if(credentials.email !== null && credentials.password !== null){
      this.authService.login(credentials.email, credentials.password).subscribe({
        next: (res) =>{
          this.user = res;
          if(res){
            this.authService.saveStorage(res);

            this.messageService.add({severity: 'success', summary: 'Success!', detail: 'Il Login è andato a buon fine', life: 3000});

            setTimeout(() => this.router.navigate(['home']), 3000);
          }
          else{
            this.loginError = "Email o password errati";
            this.messageService.add({severity: 'error', summary: 'Errore!', detail: 'Il Login non è andato a buon fine', life: 3000});
          }
        },
        error: (err) =>{
          console.log(err);
          this.loginError = "Email o password errati";
          this.messageService.add({severity: 'error', summary: 'Errore!', detail: 'Il Login non è andato a buon fine', life: 3000});
        }
    });       
  }
}



}
<div class="container-main">
    <div class="container-form">
        <form #f="ngForm" (ngSubmit)="onSubmit(f.value)">
            <h2>Accedi:</h2>
            <div class="form-element">
                <label>Email:</label>
                <br>
                <input type="text" name="email" class="form-control" ngModel>
            </div>
            <div class="form-element">
                <label>Password:</label>
                <br>
                <p-password class="form-control" [feedback]="false" [toggleMask]="true" name="password" ngModel></p-password>
            </div>
            <br>
            <button type="submit" class="btn">ENTRA</button>
            <div class="alert alert-danger" *ngIf="loginError">
                {{loginError}}
            </div>
        </form>
    </div>
    <div class="registrazione">
        <h4>Se non sei ancora registrato <a routerLink="../registrati">clicca qui</a></h4>
    </div>
</div>

<p-toast position="bottom-right"></p-toast>
