import { Component, OnInit } from '@angular/core';
import { UserService } from 'src/app/services/user.service';
import { take } from 'rxjs';
import * as moment from 'moment';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.scss']
})
export class ProfileComponent implements OnInit{

  user: any;
  data: any;
  aggiornamento: any;

  constructor(
    private userService: UserService){}

  ngOnInit(): void {
    if(JSON.parse(localStorage.getItem('user')) !== null){
      this.user = JSON.parse(localStorage.getItem('user'));
      this.onGetUser();
    }
  }


  onGetUser(){
      this.userService.getUser(this.user.mail).pipe(take(1))
      .subscribe({
        next: (res) => {
          this.user = res;
          this.data = moment(this.user.createdAt).locale('it').format('dddd DD MMMM YYYY');
          this.aggiornamento = moment(this.user.updatedAt).locale('it').format('dddd DD MMMM YYYY');
        },
        error: (err) => console.log(err)
      });
    }





}
<div class="main-container">
    <h2>Ecco i tuoi dati:</h2>
    <ul>
        <li>
            Nome: {{user.name}}
        </li>
        <li>
            Email: {{user.email}}
        </li>
        <li>note aggiuntive: {{user.note ? user.note : 'nessuna nota'}}</li>
        <li>Data di iscrizione: {{user.createdAt}}</li>
        <li>Roulo: {{user.role}}</li>
        <li>Ultima modifica dei dati: {{user.updatedAt}}</li>
    </ul>
</div>
