import { Component, OnInit } from '@angular/core';
import { take } from 'rxjs';
import { RecipeService } from 'src/app/services/recipe.service';
import { Recipe } from 'src/app/models/recipe.model';

@Component({
  selector: 'app-result',
  templateUrl: './result.component.html',
  styleUrls: ['./result.component.scss']
})
export class ResultComponent implements OnInit{

  search: any;
  ricetteTrovate: Recipe[];

  constructor(private recipeService: RecipeService){}

  ngOnInit(): void {
    this.recipeService.search.pipe(take(1))
    .subscribe({
      next: (res) => {
        this.search = res;
      },
      error: (err) => console.log(err)
    })
    this.getResults(this.search);
  }

  getResults(search: string){
    this.recipeService.getRecipesSearch(search).subscribe({
      next: (res) =>{
        this.ricetteTrovate = res;
      },
      error: (err) => console.log(err)
    })
  }


}
<div class="container-main">
    <ul *ngFor="ricette of ricetteTrovate">
        <li>
            {{ricette.name}}
        </li>
    </ul>
</div>
