import { Component, EventEmitter, Input, OnInit, Output, OnDestroy } from '@angular/core';
import { Recipe } from 'src/app/models/recipe.model';
import { RecipeService } from 'src/app/services/recipe.service';
import { Router } from '@angular/router';
import { take } from 'rxjs';

@Component({
  selector: 'app-recipe-card',
  templateUrl: './recipe-card.component.html',
  styleUrls: ['./recipe-card.component.scss']
})
export class RecipeCardComponent implements OnInit, OnDestroy{

 @Input() pag: string;
 @Output() messaggio = new EventEmitter();

 recipes: Recipe[];
 ricetteTotali: number;
 page = 1;
 ricettePerPagina = 4;

 constructor(
  private recipeService: RecipeService,
  private router: Router
  ){}


  ngOnDestroy(): void {

  }

 ngOnInit(): void {
  this.prendiRicette();
  //this.metodino();
  }

  prendiRicette(){
    this.recipeService.getRecipes()
    .pipe(
      take(1)
    )
    .subscribe({
      next: (res) => {
        this.recipes = res;

        if(this.pag){
          this.recipes = this.recipes.sort((a,b) => b._id - a._id).slice(0,4);
        }
        this.ricetteTotali = res.length;
      },
      error: (error) => {
        console.log(error);
      }
    })
  }


 inviaTitolo(titolo: string){
  this.messaggio.emit(titolo);
 }

 paginate(event){
  event.page = event.page + 1;
  this.page = event.page;
 }

//  metodino(){
//   const currentRoute = this.router.url;
//   if(currentRoute === '/home'){
//     this.prendiRicetteHome();
//   }
//   else{
//     this.prendiRicette();
//   }
//  }

// prendiRicetteHome(){
  //   this.recipeService.getRecipes().subscribe({
  //     next: (response) => {
  //       this.recipes = response;
  //       this.recipes = this.recipes.sort((a,b) => b._id - a._id).slice(0,4);
  //       this.ricetteTotali = response.length;
  //     },
  //     error: (error) => {
  //       console.log(error);
  //     }
  //   })
  // }

}

<div class="div.container-cards">
  <div class="container-pagination" *ngIf="pag!='home'">
    <div>
      <strong>{{ricettePerPagina}}</strong> Ricette su un totale di <strong>{{ricetteTotali}}</strong> ricette, pagina corrente: <strong>{{page}}</strong>
    </div>

  <p-paginator (onPageChange)="paginate($event)" [rows]="ricettePerPagina" [totalRecords]="ricetteTotali" (rowsPerPageOptionsChange)="ricettePerPagina"></p-paginator>
  </div>
  <div class="card-container" *ngFor="let recipe of recipes | slice: (page * ricettePerPagina) - ricettePerPagina : page * ricettePerPagina">
    <div class="card">
        <div class="card-image" [ngStyle]="{'background-image': 'url(' + recipe.image +')'}" (click)="inviaTitolo(recipe.title)">

        </div>
        <div class="card-body">
          <h5 class="card-title">{{recipe.title}}</h5>
          <p class="card-text">{{recipe.description| slice:0:250}}...</p>
          <a routerLink="/ricette/dettaglio/{{recipe.title}}/{{recipe._id}}" class="btn btn-primary">Visualizza</a>
        </div>
      </div>
    </div>
</div>
