import { Component } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { RecipeService } from 'src/app/services/recipe.service';

@Component({
  selector: 'app-add-recipe',
  templateUrl: './add-recipe.component.html',
  styleUrls: ['./add-recipe.component.scss']
})
export class AddRecipeComponent {

  form = new FormGroup({
    title: new FormControl('', Validators.required),
    description: new FormControl('', Validators.required),
    image: new FormControl('', Validators.required),
    difficulty: new FormControl('', Validators.required),
  })

  constructor( 
    private router: Router,
    private recipeService: RecipeService){}

  onSubmit(){
    // console.log(this.form.value);
    const newRecipe = {
     title: this.form.value.title,
     description: this.form.value.description,
     image: this.form.value.image,
     difficulty: this.form.value.difficulty,
     published: true
    }
    this.recipeService.postRecipe(newRecipe);
    this.router.navigate(['ricette']);
   }


}
<div class="container-main">
    <div class="container-form">
        <p>Inserisci una nuova ricetta</p>
        <form class="contenitore-form" [formGroup]="form" (ngSubmit)="onSubmit()">
            <div class="form-element">
                <label>Titolo ricetta:</label>
                <input type="text" id="title" name="title" class="form-control" formControlName="title"/>
                <p class="help is-danger" *ngIf="!form.get('title')?.valid && form.get('title').touched">Il campo titolo è obbligatorio</p>
            </div>

            <div class="form-element">
                <label>Descrizione ricetta:</label>
                <br>
                <textarea rows = "5" cols = "60" name = "description" formControlName="description"></textarea>
                <p class="help is-danger" *ngIf="!form.get('description')?.valid && form.get('description').touched">Il campo descrizione è obbligatorio</p>
            </div>

            <div class="form-element">
                <label>Inserisci l'url dell'immagine:</label>
                <input type="text" id="image" name="image" class="form-control" formControlName="image"/>
                <p class="help is-danger" *ngIf="!form.get('image')?.valid && form.get('image').touched">Il campo url immagine è obbligatorio</p>
            </div>

            <div class="form-element">
                <label>Inserisci la difficoltà da 1 a 5:</label>
                <input type="text" id="difficulty" name="difficulty" class="form-control" formControlName="difficulty"/>
                <p class="help is-danger" *ngIf="!form.get('difficulty')?.valid && form.get('difficulty').touched">Il campo difficoltà è obbligatorio</p>
            </div>

        </form>

    </div>

</div>
